mapper.select:
select 

from base_basetemplate

	1=1
	 AND TemplateID=#{templateid}
	 AND TemplateKey=#{templatekey}
	 AND TemplateName=#{templatename}

order by TemplateID

mapper.insert:
insert into base_basetemplate (TemplateID,TemplateKey, TemplateName, TemplateType, 
  TemplateContent, TemplateContent1, Status, 
  CreateTime, TemplateClass, TemplateDefine
  )
values (#{templateid,jdbcType=INTEGER}, #{templatekey,jdbcType=VARCHAR},#{templatename,jdbcType=VARCHAR}, #{templatetype,jdbcType=INTEGER}, 
  #{templatecontent,jdbcType=VARCHAR}, #{templatecontent1,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{createtime,jdbcType=TIMESTAMP}, #{templateclass,jdbcType=INTEGER}, #{templatedefine,jdbcType=INTEGER}
  )
